package es.avalon;

public class Calculadora {

	public static double testSuma(double a, double b) {
		// TODO Auto-generated method stub
		return a+b;
	}
	
	public static double testResta(double a, double b) {
		// TODO Auto-generated method stub
		return a-b;
	}

	public static double testProducto(double a, double b) {
		// TODO Auto-generated method stub
		return a*b;
	}

	public static double testDivision(double a, double b) {
		// TODO Auto-generated method stub
		return a/b;
	}	
	
	
}
